                    <div class="estrelas">
                    <?php
                    
                        $sql = "SELECT 
                                pags.url, 
                                COUNT(avaliacoes.id_user) AS qt, 
                                AVG(avaliacoes.qnt_estrelas) AS estrelas
                                FROM pags 
                                INNER JOIN avaliacoes ON pags.id = avaliacoes.id_prod
                                WHERE pags.url = '$url'
                                GROUP BY pags.url";
                        $res = $PDO->query( $sql );
                        $row = $res->fetchAll(PDO::FETCH_ASSOC);
                        foreach ($row as $key => $linha) {
                            
                            $qt      = $linha["qt"];
                            $score   = number_format($linha["estrelas"],0,',','.');
                        }
                        
                        if(!isset($qt)){
                            $qtu = 0;
                            $star = 0;        
                        } else {
                            $qtu = $qt;
                            $star = $score;       
                            
                        }
                    ?>
                        Avaliação de nossos leitores <span style="font-size: 10pt; color: #999;"><sup>(<?php echo $qtu; ?>)</sup></span>
                        <i class="fa fa-star" style="<?php if($star == 0){ echo "color: #ccc"; } else { echo "color: #fc0"; } ?>" ></i>
